<?php

// marker starts at count 10
$input = 'nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg';

$input= '';

function getStartMarker($message, $stacksize)
{
    $stack = [];
    $count = 0;
    for ($i=0; $i<strlen($message); $i++) {
        $count++;
        
        if (count($stack)==$stacksize) {
            array_shift($stack);            
        }

        $stack[] = $message[$i];        
        if (count($stack)<$stacksize) continue;
        if (count($stack) == count(array_unique($stack))) {
            return $count;
        }
    }
}

var_dump(getStartMarker($input, 4));
var_dump(getStartMarker($input, 14));

